import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // State for storing messages
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [visitorCount, setVisitorCount] = useState(0);

  // Function to handle adding a new message
  const handleAddMessage = () => {
    if (newMessage.trim()) {
      setMessages([...messages, newMessage]);
      setNewMessage('');
    }
  };

  // Update visitor count on component mount
  useEffect(() => {
    const count = localStorage.getItem('visitorCount');
    if (count) {
      setVisitorCount(parseInt(count));
    }
    localStorage.setItem('visitorCount', visitorCount + 1);
    setVisitorCount(1);
  }, [visitorCount]);

  return (
    <div className="App">
      <header className="App-header">
        {/* Photograph */}
        <img src="a.png" alt="Your Name" className="profile-photo" />

        {/* Introduction */}
        <h1>Your Name</h1>
        <p>This is a brief introduction about yourself. You can mention your hobbies, profession, or anything else!</p>

        {/* Visitor Counter */}
        <div className="visitor-counter">
          <p>Visitor Count: {visitorCount}</p>
        </div>

        {/* Message Board */}
        <div className="message-board">
          <h2>Message Board</h2>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Leave a message"
          />
          <button onClick={handleAddMessage}>Add Message</button>
          <div className="messages">
            {messages.map((message, index) => (
              <div key={index} className="message">
                <p>{message}</p>
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}




export default App;
